package com.example.a360refo.ui.home;

import android.os.Build;
import android.os.Bundle;
import android.util.Log;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.Button;

import androidx.annotation.NonNull;
import androidx.annotation.RequiresApi;
import androidx.fragment.app.Fragment;

import com.example.a360refo.R;
import com.example.a360refo.ui.body.BodyFragment;
import com.smarteist.autoimageslider.SliderView;

import java.util.ArrayList;

public class HomeFragment extends Fragment {

    String url1="data:image/jpeg;base64,";
    String url2="data:image/jpeg;base64,";
    String url3="data:image/jpeg;base64,";
    private ViewGroup mContainer;


    @RequiresApi(api = Build.VERSION_CODES.JELLY_BEAN_MR1)
    public View onCreateView(@NonNull LayoutInflater inflater,
                             ViewGroup container, Bundle savedInstanceState) {

        View root = inflater.inflate(R.layout.fragment_home, container, false);
        mContainer = container;
        // we are creating array list for storing our image urls.
        ArrayList<HomeViewModel> sliderDataArrayList = new ArrayList<>();

        // initializing the slider view.
        SliderView sliderView = root.findViewById(R.id.slider);
        Button joinUs = root.findViewById(R.id.joinUs);

        // adding the urls inside array list
        sliderDataArrayList.add(new HomeViewModel(url1));
        sliderDataArrayList.add(new HomeViewModel(url2));
        sliderDataArrayList.add(new HomeViewModel(url3));


        // passing this array list inside our adapter class.
        SliderAdapter adapter = new SliderAdapter(this , sliderDataArrayList);

        // below method is used to set auto cycle direction in left to
        // right direction you can change according to requirement.
        sliderView.setAutoCycleDirection(SliderView.LAYOUT_DIRECTION_LTR);

        // below method is used to
        // setAdapter to sliderView.
        sliderView.setSliderAdapter(adapter);

        // below method is use to set
        // scroll time in seconds.
        sliderView.setScrollTimeInSec(2);

        // to set it scrollable automatically
        // we use below method.
        sliderView.setAutoCycle(true);

        // to start autoCycle below method is used.
        sliderView.startAutoCycle();

        joinUs.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View v) {
                getFragmentManager().beginTransaction()
                        .replace(container.getId(), (Fragment) new BodyFragment())
                        .commit();
                Log.d("360ReFo", "Switched to body");
            }
        });
        return root;
    }

}